def should_hit(player_total, dealer_card_val, player_aces):
    """Return True if the player should hit (request another card) given the current game
    state, or False if the player should stay. player_aces is the number of aces the player has.
    """
#soft
    if player_aces >= 1:        
        if player_total >= 18:
            return False
        if player_total == 18 and dealer_card_val == 9:
            return True
        if dealer_card_val >= 7:
            return True
        if player_total <= 15:
            return True
    if player_aces == 0:
        if player_total >= 16:
            return False
        if dealer_card_val >= 7:
            return True
        if player_total <= 12:
            return True
        return False

