import os
import re


files = "../input/top-10-values"
values = []
def read_file(path):
    value = []
    with open(path,'r') as value_file:
        for line in value_file:
            if not line.isspace():
                value.append(line)
    values.extend(value)
for file in os.listdir(files):
    if file.endswith(".txt") and file.startswith("F"):
        path = f"{files}/{file}"
        read_file(path)

