def extract_entity(text):
    if text.ents:
        for entity in text.ents:
            print(entity.text + ' - ' + str(entity.start_char) + ' - ' + str(entity.end_char) + ' - ' + entity.label_ + ' - ' + str(spacy.explain(entity.label_)))

    else:
        print('No Named entites found.')
