# Plots on numerical features to check data quality and data distribution

num_cols=pages_data.select_dtypes(include=['int64','float64']).columns.tolist() # a revised list of numerical features  

for i in num_cols:

    fig, axs = plt.subplots(1,2,figsize=(15, 3))

    sns.histplot(pages_data[i],bins=20, kde=True,ax=axs[0]);
    sns.boxplot(pages_data[i], ax = axs[1], color='#99befd', fliersize=1);

    axs[0].axvline(pages_data[i].median(), color='r', linewidth=2, linestyle='--', label='Mean')
    axs[0].legend()
