def get_csv_from_html(soup):
    """
    Scrape table from html page, convert table to dataframe and return it
    """
    data = []
    list_header = []
    header = soup.find("tr")

    for items in header:
        try:
            list_header.append(
                items.get_text().replace('\n','').strip()
            )
        except Exception as e:
            continue

    html_data = soup.find_all("tr")[1:]

    for element in html_data:
        sub_data = []
        for sub_element in element:
            try:
                sub_data.append(sub_element.get_text().strip())
            except Exception as e:
                continue
        data.append(sub_data)

    df = pd.DataFrame(data=data, columns=list_header)
    return df
