def eigenvector(g):
    eigenvector = g.evcent() 
    df = pd.DataFrame.from_dict({
        'word': list({v['name']: v.index for v in list(g.vs)}),
        'eigenvector': list(g.evcent())
    })
    return df.sort_values('eigenvector', ascending=False)

