def eval_model(preds, real, ts):
    score=0
    for T in TYPES:
        bool_t = ts==T
        nt = len(preds[bool_t])
        temp = np.sum(np.abs(preds[bool_t]-real[bool_t]) / nt)
        score += np.log(temp)
    return score/(len(TYPES))
