import re
def get_return_yards(s):
    m = re.search('for ([0-9]+) yards', s)
    if m:
        return int(m.group(1))
    elif re.search('for no gain', s):
        return 0
    else:
        return np.nan

play_info['return_yards'] = play_info['playdescription'].map(
        lambda x: get_return_yards(x))
