map_clip = {'Lead':8, 'Position':0, 'Evidence':19, 'Claim': 3, 'Concluding Statement':13,
            'Counterclaim':6, 'Rebuttal':7}

def threshold(df):
    df = df.copy()
    for key, value in map_clip.items():
    # if df.loc[df['class']==key,'len'] < value 
        index = df.loc[df['class']==key].query(f'len<{value}').index
        df.drop(index, inplace = True)
    return df

