def get_job_class_no(t):
    try:
        m = re.search(r'Class Code:[ ]*([0-9]*)', t)
        return m.group(1).zfill(4)
    except:
        return -1
job_bulletins['JOB_CLASS_NO'] = job_bulletins['Text'].map(lambda x: get_job_class_no(str(x)))
job_bulletins.head(1)

