jahli_df = ar_poems.loc[ar_poems['poet_cat'] == "العصر الجاهلي"]
jahli_text = []
jahli_text.extend(jahli_df["poem_text"])

jahli_animals = 0
for animal_word in animal_words:
    for word in jahli_text:
        if animal_word in word:
            jahli_animals += 1

jahli_nature = 0
for nature_word in nature_words:
        for word in jahli_text:
            if nature_word in word:
                jahli_nature += 1

jahli_meta = 0
for meta_word in meta_words:
    for word in jahli_text:
        if meta_word in word:
            jahli_meta += 1

jahli_rows = jahli_df.shape[0]
jahli_rate_animal = jahli_animals/jahli_rows
jahli_rate_nature = jahli_nature/jahli_rows
jahli_rate_meta = jahli_meta/jahli_rows

print("عدد تكرار أسماء الحيوانات بالشعر الجاهلي هو :", jahli_animals, "مرة", "وبمعدل" , jahli_rate_animal)
print("عدد تكرار أسماء عناصر الطبيعة بالشعر الجاهلي هو :", jahli_nature, "مرة", "وبمعدل" , jahli_rate_nature)
print("عدد تكرار الأسماء الميتافيزيقية بالشعر الجاهلي هو :", jahli_meta, "مرة", "وبمعدل" , jahli_rate_meta)

