#extracting the URL to clean what can be analysed
def get_url(x):
  urls = re.findall('http[s]?://(?:[a-zA-Z]|[0-9]|[$-_@.&+]|[!*\(\),]|(?:%[0-9a-fA-F][0-9a-fA-F]))+', x)

  if len(urls)==0:
    return ' '
  else:
    return urls

