#extracting the Tags to be able to clearer
def tag(x):
  hashtag = set(part[1:] for part in x.split() if part.startswith('#'))

  if len(hashtag)==0:
    return ' '
  else:
    return hashtag

df['tags'] = df['text1'].apply(lambda x: tag(x) )
df['tags'] = [' '.join(map(str, l)) for l in df['tags']]
df.sample(3)

