#extracting the follow or subscribe info on a specific column
def prom(x):
  promoting = re.findall('subscribe | follow | promo', x)

  if len(promoting)==0:
    return ' '
  else:
    return promoting

df['promote'] = df['text1'].apply(lambda x: prom(x) )
df['promote'] = [' '.join(map(str, l)) for l in df['promote']]
df.sample(3)
