from IPython.display import Image, display
def show_image(img):
    image = Image(img)
    display(image)

showcount = 3
for dirname, _, filenames in os.walk('/kaggle/input'):
    for filename in filenames:
        show_image(os.path.join(dirname, filename))
        showcount -= 1
        if showcount < 1:
            break
