def GetGrade(Percentage, OverAllPassStudents):
    if ( OverAllPassStudents == 'F'):
        return 'F'    
    if ( Percentage >= 80 ):
        return 'A'
    if ( Percentage >= 70):
        return 'B'
    if ( Percentage >= 60):
        return 'C'
    if ( Percentage >= 50):
        return 'D'
    if ( Percentage >= 40):
        return 'E'
    else: 
        return 'F'

dataset['Grade'] = dataset.apply(lambda x : GetGrade(x['Percentage'], x['OverAllPassStudents']), axis=1)

