def count(var):
    val=var.value_counts()
    return val
def unique(variable):
    value=variable.unique()
    return value

#for Numerical
def fillna_numerical(var):
    value=var.fillna(var.mean(),inplace=True)
    return value
#for categorical
def fillna_cat(var):
    value=var.fillna(var.mode(),inplace=True)
