def remove_spaces(vals):
  if (' ' in vals):
    return vals.replace(' ','')
  elif('  ' in vals):
    return vals.replace('  ','')
  else:
    return vals

dor['State_UT']=dor['State_UT'].apply(func=remove_spaces)

