def is_grey_scale(givenImage):
    """Adopted from https://www.kaggle.com/lextoumbourou/humpback-whale-id-data-and-aug-exploration"""
    w,h = givenImage.size
    for i in range(w):
        for j in range(h):
            r,g,b = givenImage.getpixel((i,j))
            if r != g != b: return False
    return True

