def boxcox(X):    
    cols = [feature for feature in X.columns if X[feature].dtype != 'O' and feature != 'SalePrice']

    for feature in cols:
        if (skew(X[feature]) < -0.5) or (skew(X[feature]) > 0.5):
            X[feature] = pd.Series(boxcox1p(X[feature], .3), index=X.index)

    return X

