# Categorize the number of votes for each anime in 6 bins
def create_bins(v):
    if v > 10000:
        return ">10000"
    elif v > 2000:
        return "2000-10000"
    elif v > 500:
        return "500-2000"
    elif v > 100:
        return "100-500"
    elif v >= 10:
        return "10-100"
    else:
        return "<10"

df["votes_cat"] = df["votes"].apply(create_bins)
