def height_categ(x):
    """
    """
    a=x.split('-')
    if len(a) > 1:
        a=float(a[0])+float(a[1])/12
        if (a<6.0):
            return 'below 6'
        elif (a>=6.0) & (a<=6.5):
            return '6 to 6.5 feet'
        elif (a>6.5) & (a<7.0):
            return '6.5 to 7 feet'
        elif (a>=7.0):
            return 'over 7'
    else:
        a=x
        a=a.replace('cm',' ')
        a=float(a)*0.0328084
