#For EDA
def buckets(year):
    if 1950<=year<=1970:
        return '1960s'
    elif 1970<=year<=1990:
        return '1980s'
    elif 1990<=year<=2010:
        return '2001s'

#Age of house
df['Age'] = df['YrSold'] - df['YearBuilt']
df['Year Mod'] = df['YearRemodAdd'].apply(buckets)
