#function to classify Clubs based on League
def league_setter(team):
    if team in EPL:
        return "EPL"
    elif team in LaLiga:
        return "La Liga"
    elif team in SerieA:
        return "Serie A"
    elif team in Bundesliga:
        return "Bundesliga"
    else:
        return "League 1"
