#finding all numerical values within the "incubation" sentences
for t in text:
    for sentence in t.split(". "):
        if "incubation" in sentence:
            iday=re.findall(r" \d{1,2} day| \d{1,2}.\d{1,2} day",sentence)
            if len(iday)==1:
                num=iday[0].split(" ")
                incubation_times.append(num[1])

