def group_birth(year):
    if year >= 2005 and year <= 2019 :
        return 'Children'
    elif year >= 1995 and year <= 2004:
        return 'Youth'
    elif year >= 1955 and year <= 1994:
        return 'Adult'
    else:
        return 'Senior'

bikes['age_group'] = bikes['member_birth_year'].apply(group_birth)

