# check whether password contains "password" case insensitive
def extract_string_password(words):
    strings = []
    others = []
    for word in words:
        if word.islower() == True or word.isupper() == True:
            strings.append(word)
        else:
            others.append(word)
    result = ''.join(strings)
    if 'password' in result.lower():
        return True
    else:
        return False

