def check_for_first_last_name(df, bad_pws):
    for i, rows in df.iterrows():
        if rows.first_name in rows.password.lower() or rows.last_name in rows.password.lower():
            df = df.drop(index=i)
            bad_pws = bad_pws.append(rows)
    return df, bad_pws
