df['Contract_months']=df['Contract_months'].astype('int')
df['renewals']=df['tenure']/df['Contract_months']

segment3 = []

for i in df['renewals']:
    if (i < 1):
        segment3.append('Before renewal')
    elif (i >=1)&(i<5):
        segment3.append('1 - 5 times')
    elif (i >=5)&(i<10):
        segment3.append('5 - 10 times')
    elif (i >=10)&(i<15):
        segment3.append('10 - 15 times')
    elif (i >=15)&(i<20):
        segment3.append('15 - 20 times')
    elif (i >=20):
        segment3.append('More than 20 times')

df['renewal seg']=segment3



segment4=[]
for i in df['tenure']:
    if (i <= 6):
        segment4.append('Before 6 months')
    elif (i >6)&(i<=12):
        segment4.append('Between 6 - 12 months')
    elif (i >12)&(i<=18):
        segment4.append('Between 1 - 1.5 years')
    elif (i >18)&(i<=24):
        segment4.append('Between 1.5 - 2 years')
    elif (i >24)&(i<=36):
        segment4.append('Between 2 - 3 years')
    elif (i >36)&(i<=48):
        segment4.append('Between 3 - 4 years')
    elif (i >48)&(i<=72):
        segment4.append('Between 4 - 6 years')

df['tenure segment']=segment4



