def crps(y_true, y_pred):
    ans = 0
    ground_t = y_true.argmax(1)
    for i, t in enumerate(ground_t):
        for n in range(-99, 100):
            h = n>=(t-99)

            ans+=(y_pred[i][n+99]-h)**2

    return ans/(199*len(y_true))
