def get_status(x):
    if(pd.isnull(x)):
        return np.nan
    else:
        x = x.upper()

    if('BORDO' in x):
        return 'ABORDO'
    elif('SENT' in x):
        return 'ISENTO'
    elif(re.search('[^0-9]', x)):
        return np.nan
    else:
        return 'NORMAL'

aircrafts['annual_inspec_status'] = aircrafts['exp_date_annual_inspection'].apply(get_status)

