# Making a function to find the frequency of the words
def frequency(sentence,number):
    dic={}
    for i in sentence:
        if i in dic:
            dic[i]+=1
        else:
            dic[i]=1
    res = dict(sorted(dic.items(), key = itemgetter(1), reverse = True)[:number])
    return res

