from IPython.core.display import HTML
text = ''

display(HTML(f'<h1>Text summary</h1>'))
for sentence in sentence_list:

  if sentence in best_sentences:
    text += str(sentence).replace(sentence, f"<mark>{sentence}</mark>")
  else:
    text += sentence
display(HTML(f"""{text}"""))
