def CGPA(Percentage):
    if Percentage >= 91:
        return 'S'
    elif Percentage >= 81:
        return 'A'
    elif Percentage >= 71:
        return 'B'
    elif Percentage >= 61:
        return 'C'
    elif Percentage >= 51:
        return 'D'
    elif Percentage >= 41:
        return 'E'
    else:
        return 'F'
