# 2*3の図を作成する
f, axes = plt.subplots(2, 3, figsize=(9,6))
for ax_y in range(2):
    for ax_x in range(3):
        x = np.arange(50) * 0.1
        # axes[y, x]で指定する
        if ax_y == 0:
           y = np.sin(x * (ax_x + 1))
        else:
           y = np.cos(x * (ax_x + 1))
        axes[ax_y, ax_x].plot(x, y)

plt.show()
