import random
import numpy as np 

class LottoPick:

    def __init__(self,totalNum,choiceNum):
        self.totalNum = totalNum
        self.choiceNum = choiceNum

    def __call__(self):
        return self.pick()

    def pick(self):
        return sorted([np.random.choice(self.totalNum , self.choiceNum, replace=False)])
