def load_png_line(path):
    t_paths = sorted(
        glob.glob(os.path.join(path,"*")), 
        key=lambda x: x,
    )
    images = []
    for filename in tqdm(np.array(t_paths)):
        data = cv2.imread(filename)
        if data.max() == 0:
            continue
        images.append(data)

    return images

