def population_random_walk_1(n):
    for i in range(n):
        for a in population[population["status"]=="human"].index:
            population.loc[a,"x"]= population.loc[a,"x"]+np.random.choice([-1,1])
            population.loc[a,"y"]= population.loc[a,"y"]+np.random.choice([-1,1])

