def getting_user_location(name):
    x=data[name].dropna().values
    dic={}
    for i in x:
        if i==np.nan:
            continue
        else:
            if i.split(',')[-1].replace(' ','') in dic:
                dic[i.split(',')[-1].replace(' ','')]+=1
            else:
                dic[i.split(',')[-1].replace(' ','')]=1
    dic=dict(sorted(dic.items(), key=lambda item: item[1],reverse=True))
    return dic

