def experience(job):
    for w in ["Junior Data Analyst", "Jr.", "Data Analyst I", "Jr", "1"]:
        if w in job:
            return "Junior"    
    for w in ["Senior", "III", "Lead", "Sr", "Sr.", "3", "Principal", "Master"]:
        if w in job:
            return "Senior"
    else:
        return "Regular/Other"

data["Exp. Level"] = data["Job Title 1"].apply(experience)

