def bs_(list_, target_):
    lo, hi = 0, len(list_) -1

    while lo < hi:
        mid = lo + int((hi - lo) / 2)

        if target_ < list_[mid]:
            hi = mid
        elif target_ > list_[mid]:
            lo = mid + 1
        else:
            return mid
    return lo

def ohe_dist(dist, buckets):
    idx = bs_(buckets, dist)
    oh = np.zeros(shape=(len(buckets),), dtype=np.float32)
    oh[idx] = 1

    return oh

