# Add total wind magnitude
for d in data:
    if d['measurement'] == 'weather':
        r = rio.open(d['path'])
        d.update({
            'avg_wind_magnitude': 
            np.nanmean(np.sqrt(np.square(r.read(4)) + np.square(r.read(5)))),
            'max_wind_magnitude': 
            np.nanmax(np.sqrt(np.square(r.read(4)) + np.square(r.read(5))))
        })
        r.close()

