try:
    response = v3_client.translate_text(
            request={
                "parent": parent_resource,
                "contents": ["Hello world","Hola Mundo"],
                "mime_type": "text/plain",  # mime types can be: text/plain, text/html
                "target_language_code": "fr",
            }
        )

    display(response)
except InvalidArgument as ex:
    print(f"Please check your 'gcp_project_id' variable is set correctly. \n{ex}")

