try:
    response = v3_client.get_supported_languages(parent=parent_resource)

    # List language codes of supported languages.
    print("Supported Languages:")
    for language in response.languages:
        print(language)
except InvalidArgument as ex:
    print(f"Please check your 'gcp_project_id' variable is set correctly. \n{ex}")

