def filter(x):
    a = [None] * len(x)
    for i in range(0,(len(x)-1)):
        list3=[]
        blob = word_tokenize(x.iloc[i][1])
        for word in blob:
            if word not in stop_words:
                list3.append(word)
            a[i]=list3
    x["filter"]=a
