def bitis_yili_bucketize(x):
    if (x >= 1910) and (x < 2005):
        return 0
    elif (x >= 2005) and (x < 2010):
        return 1
    elif (x >= 2010) and (x < 2015):
        return 2
    elif (x >= 2015 and x < 2020):
        return 3
    else:
        return 4

