def get_percentage(job_name):
    df2 = df1[df1["Q5"]==job_name][["Q4","Q5"]].groupby("Q4").count().reset_index()
    df2["%"] = 100 * df2["Q5"]/df2["Q5"].sum()
    return df2.sort_values("Q4",key=lambda x:x.map(d))
