Ga = nx.Graph()
for item in id2bib[0:10]:
    Ga.add_node(item['id'],title = item['title'])
    for ref in item['bib']:
        Ga.add_node(ref['DOI'], title = ref['title'], year = ref['year'], venue = ref['venue'])
        Ga.add_edge(item['id'], ref['DOI'], value = ref['refid'])

