def agelimit(row):
    if row['Age'] <= 18:
        return "children"
    elif row['Age'] >= 18.1 and row['Age'] <= 30:
        return "youngadult"
    elif row['Age'] >= 30.1 and row['Age'] <= 50:
        return "middleage"
    else:
        return "elderly"

