def fit(row):
    if row['bmi'] < 18.5:
        return "underweight"
    elif row['bmi'] >= 18.5 and row['bmi'] <= 24.9:
        return "fit"
    elif row['bmi'] >= 25 and row['bmi'] <= 29.9:
        return "overweight"
    else:
        return "obesity"
